import React, { useState } from 'react';
import style from "./style.module.css";



const Form = () => {
    const [fullURL, setFullURL] = useState("");
    const [customURL, setCustomURL] = useState("");
    const [customUrlMode, setCustomUrlMode] = useState(false);
    const onChangeFullURL = event => setFullURL(event.target.value);
    const onChangeCustomURL = event => setCustomURL(event.target.value);
    const onChangeShrinkMode = () => {
      setCustomUrlMode(!customUrlMode);
      };

const CustomUrlInput = ()=>{
  return(
    <div className={style.input}>
    <input 
     placeholder = "Желаемый URL"
    className={style.inputComponent}
    type="text" 
    value={customURL} 
    onChange={onChangeCustomURL}
    />
    </div>
  );
}

  return (
    <div className={style.wrapper}>
      <div className={style.form}>
        <div className={style.logo}>
          <p className={style.icon}>✂️</p>
          <p className={style.title}>URL урезатель</p>
        </div>
        <div className={style.label}>
          <p className={style.top}>Поместите в поле ввода свой URL</p>
          <p className={style.bottom}>на его основе будет создан короткий URL</p>
        </div>
        <div className={style.input}>
        <div>
        <input 
        placeholder = "Полный URL"
        className={style.inputComponent}
        type="text" 
        value={fullURL} 
        onChange={onChangeFullURL}
      />
      </div>
       <div>
       <p className={style.inputLabel_checkbox}>Задать пользовательский короткий URL</p>
      <input type="checkbox" id="customURL" name="customURLcheck"
      className={style.checkbox}
       onClick={onChangeShrinkMode}
         ></input>
         </div>
         <div>
         { customUrlMode ? <CustomUrlInput/>: null}
         </div>
        </div>
        <div className={style.button}>
          <button className={style.buttonComponent} onClick = {()=>console.log("pepepopo")}>Сократить URL</button>
        </div>
      </div>
    </div>
  );
};

export default Form;
