import React, { useState } from 'react';
import style from "./style.module.css";

const Form = () => {
    const [amoung, setAmoung] = useState("");
    const [customURL, setCustomURL] = useState(false);
    const onChangeURL = event => setAmoung(event.target.value);
    const onChangeShrinkMode = event => setCustomURL(event.target.value);



  return (
    <div className={style.wrapper}>
      <div className={style.form}>
        <div className={style.logo}>
          <p className={style.icon}>✂️</p>
          <p className={style.title}>URL урезатель</p>
        </div>
        <div className={style.label}>
          <p className={style.top}>Генератор данных для тестирования</p>
          <p className={style.bottom}>Данны о посетителях столовой (кроме Восточной)</p>
        </div>
        <div className={style.input}>
        <p className={style.inputLabel}>Введите число студентов:</p>
        <input 
        className={style.inputComponent}
        type="text" 
        value={amoung} 
        onChange={onChangeURL}
      />
      <input type="checkbox" id="customURL" name="customURLcheck"
       onChange={onChangeShrinkMode}
         ></input>
        </div>
        <div className={style.button}>
          <button className={style.buttonComponent} onClick = {()=>console.log("pepepopo")}>Скачать данные</button>
        </div>
      </div>
    </div>
  );
};

export default Form;
